// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;
pragma abicoder v2;


contract SafeToken{

    uint256 constant private MAX_UINT256 = 2**256 - 1;
    mapping (address => uint256) public balances;
    mapping (address => mapping (address => uint256)) public allowed;

    event Transfer(address indexed _from, address indexed _to, uint256 _value);
    event Approval(address indexed _owner, address indexed _spender, uint256 _value);

    string public name = "Safe token";
    uint8 public decimals = 18;
    string public symbol = "SAFE";
    uint public totalSupply;

    address public minter;
    uint public firstTransferTime;
    uint public epochTokensTransferred;
    uint public maxSupplyTransferPercentage;
    uint public epochDelay;

    uint public currentTick = 0;
    mapping(address => uint)[] public todayTransfers;

    constructor(address _minter, uint _maxSupplyTransferPercentage, uint _totalSupply, uint _epochDelay){
        totalSupply = _totalSupply;
        minter = _minter;
        epochDelay = _epochDelay;
        firstTransferTime = block.timestamp;
        balances[_minter] = _totalSupply;
        todayTransfers.push({});
        maxSupplyTransferPercentage = _maxSupplyTransferPercentage;
    }

    function getTotalTransferLimit() public view returns(uint){
        return (totalSupply * maxSupplyTransferPercentage) / 100;
    }

    function burn(uint amount) external{
        require(balances[msg.sender] >= amount, "LB");
        transferLock(amount);
        balances[msg.sender] -= amount;
        totalSupply -= amount;
    }

    function getUserTransferLimit() public view returns(uint){
        return totalSupply * maxSupplyTransferPercentage / 10000;   // TODO: calculate max transfers
    }

    function isEpochOver() public view returns(bool){
        return uint(block.timestamp) - firstTransferTime >= epochDelay;
    }

    function transferLock(uint transferAmount) private {
        if(msg.sender == minter){   // minter contract can transfer as many tokens as it wants
            return;
        }

        epochTokensTransferred += transferAmount;
        uint transferLimit = getUserTransferLimit();

        todayTransfers[currentTick][msg.sender] += transferAmount;
        require(todayTransfers[currentTick][msg.sender] <= transferLimit, "LE");    // LIMIT EXCEEDED

        if(isEpochOver()){

            currentTick++;
            epochTokensTransferred = transferAmount;
            firstTransferTime = block.timestamp;
            todayTransfers.push({});
            todayTransfers[currentTick][msg.sender] = transferAmount;

        }else if(epochTokensTransferred >= getTotalTransferLimit()){
            revert("TLE");  // TOTAL LIMIT EXCEEDED
        }

    }

    function transfer(address _to, uint256 _value) external returns (bool success) {
        require(balances[msg.sender] >= _value, "LB");
        transferLock(_value);

        balances[msg.sender] -= _value;
        balances[_to] += _value;        

        emit Transfer(msg.sender, _to, _value); //solhint-disable-line indent, no-unused-vars
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) external returns (bool success) {
        uint256 allowance_ = allowed[_from][msg.sender];
        require(balances[_from] >= _value && allowance_ >= _value, "LB");
        transferLock(_value);

        balances[_to] += _value;
        balances[_from] -= _value;

        if (allowance_ < MAX_UINT256) {
            allowed[_from][msg.sender] -= _value;
        }

        emit Transfer(_from, _to, _value); //solhint-disable-line indent, no-unused-vars
        return true;
    }

    function balanceOf(address _owner) external view returns (uint256 balance) {
        return balances[_owner];
    }

    function approve(address _spender, uint256 _value) external returns (bool success) {
        allowed[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value); //solhint-disable-line indent, no-unused-vars
        return true;
    }

    function allowance(address _owner, address _spender) external view returns (uint256 remaining) {
        return allowed[_owner][_spender];
    }

}
